<script>
	import { Label, Input, Checkbox, Button } from 'flowbite-svelte';

	let loginInput = {
		email: '',
		password: ''
	}

	const login = () => {
		if (loginInput.email === '' || loginInput.password === '') {
			alert('이메일 또는 비밀번호를 입력해 주세요.');
			return;
		}

		fetch('/login/user', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({'email':loginInput.email, 'password':loginInput.password})
		})
			.then(response => {
				if (!response.ok) {
					alert('아이디 비밀번호가 올바르지 않습니다.');
					return;
				}

				return response.json();
			})
			.then(response => {
				if (response) {
					window.location.href = "/main";
				}
			})
			.catch(e => console.log(e));
	}

	const socialLogin = type => {
		location.href = `/oauth/${type}`;
	}
</script>

<section>
	<div class="form-wrap">
		<label class="login-title">Welcome!</label>
		<div class="login-input-wrap">
			<div class="mb-6">
				<Label for="email" class="mb-2">Email</Label>
				<Input type="email" id="email" placeholder="voa@voa.com" required="" bind:value={loginInput.email} />
			</div>
			<div class="mb-6">
				<Label for="password" class="mb-2">Password</Label>
				<Input type="password" id="password" required="" bind:value={loginInput.password} />
			</div>
			<Checkbox class="mb-6">Remember me</Checkbox>
		</div>
		<Button color="blue" type="submit" on:click={login}>Login</Button>
		<hr/>
		<div class="social-login-wrap">
			<div id="google-login-btn" class="icon button google" on:click={() => socialLogin('google')}></div>
			<div id="naver-login-btn" class="icon button naver" on:click={() => socialLogin('naver')}></div>
			<div id="kakao-login-btn" class="icon button kakao" on:click={() => socialLogin('kakao')}></div>
		</div>
	</div>
</section>

